commits.content:
body{
    font-family: 'Arial', sans-serif;
    background-color: #ffe5b4;
    color: #6d4c41;
    margin: 0;
    text-align: center;
}
header{
    background-color: #FFCC99;
    padding: 15px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.logo-contenedor h1{
    margin: 0;
}
nav a{
    text-decoration: none;
    color: #6d4c41;
    font-weight: bold;
    margin:0 15px;
}
.boton_autenticacion{
    background-color: #FF9966;
    border:none ;
    color: white;
    padding: 10px 15px;
    border-radius: 5px;
    cursor: pointer;
}
.boton_autenticacion:hover{
    background-color: #FF7043;
}
.seccion-principal{
    background-color: #ffdab9;
    padding: 50px 20px;
}

.contenedor-categorias{
    display: flex;
    justify-content: center;
    gap: 20px;

}
.tarjeta-categoria{
    text-decoration: none;
    background: white;
    padding: 20px;
    border-radius: 10px;
    color: #6d4c41;
    font-size: 1.5rem;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
    transition: transform 0.2s ease-in-out;
}
.tarjeta-categoria:hover{
    transform: scale(1.1);
}
.seccion-destacados, .seccion-testimonios, .seccion-adopcion {
    padding: 40px;
    background-color: #FFF3E0;
}
.seccion-destacados, .contenedor-pasos{
    display: flex;
    justify-content: center;
    gap: 20px;
    flex-wrap: wrap;
    display: flex;
}
.tarjeta-mascota, .tarjeta-testimonio, .paso{
    background-color: white;
    border-radius: 10px;
    padding: 15px;
    margin :15px;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 250px;
}
.tarjeta-mascota img{
    width: 100%;
    border-radius: 10px;
}
.tarjeta-mascota button{
    background-color: #FF9966;
    border: none;
    color: white;
    padding:10px;
    border-radius: 5px;
    cursor:pointer;
}
.tarjeta-mascota button:hover {
    background-color: #FF7043;
}
.pie-pagina {
    background-color: #8D6E63;
    color: white;
    padding: 10px;
    margin-top: 20px;
}
