body{
    font-family: 'Arial', sans-serif;
    background-color: #ffe5b4;
    color: #6d4c41;
    margin: 0;
    text-align: center;
}
header{
    background-color: #FFCC99;
    padding: 15px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.logo h1{
    margin: 0;
}
nav a{
    text-decoration: none;
    color: #6d4c41;
    font-weight: bold;
    margin:0 15px;
}
.boton-autenticacion{
    background-color: #FF9966;
    border:none ;
    color: white;
    padding: 10px 15px;
    border-radius: 5px;
    cursor: pointer;
}
.boton-autenticacion:hover{
    background-color: #FF7043;
}
.seccion-principal{
    background-color: #ffdab9;
    padding: 50px 20px;
}

.contenedor-categorias{
    display: flex;
    justify-content: center;
    gap: 20px;
}
.tarjeta-categoria{
    text-decoration: none;
    background: white;
    padding: 20px;
    border-radius: 10px;
    color: #6d4c41;
    font-size: 1.5rem;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
    transition: transform 0.2s ease-in-out;
}
.tarjeta-categoria:hover{
    transform: scale(1.1);
}
.seccion-destacados, .contenedor-pasos{
    display: flex;
    justify-content: center;
    gap: 20px;
    flex-wrap: wrap;
}
.tarjeta-mascota img{
    width: 100%;
    border-radius: 10px;
}
