body{
    font-family: 'Arial', sans-serif;
    background-color: #ffe5b4;
    color: #6d4c41;
    margin: 0;
    text-align: center;
}
#logo{
    width: 120px;
    height: 120px;
    object-fit: contain;
}
header{
    background-color: #FFCC99;
    padding: 15px 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.menu-centro {
    flex: 1;
    display: flex;
    justify-content: center;
}
.menu-centro ul {
    list-style: none;
    display: flex;
    gap: 20px;
    margin: 0;
    padding: 0;
}
.menu-centro ul li a {
    text-decoration: none;
    color: #6d4c41;
    font-weight: bold;
}
.btn-session{
    display: flex;
    justify-content: flex-end;
    margin-left: auto;
}
.logo-contenedor h1{
    margin: 0;
}
.boton-autenticacion{
    background-color: #FF9966;
    border:none ;
    color: white;
    padding: 10px 15px;
    border-radius: 5px;
    cursor: pointer;
}
.boton-autenticacion:hover{
    background-color: #FF7043;
}
.seccion-principal{
    background-color: #ffdab9;
    padding: 50px 20px;
}

.contenedor-categorias{
    display: flex;
    justify-content: center;
    gap: 20px;

}
.tarjeta-categoria{
    text-decoration: none;
    background: white;
    padding: 20px;
    border-radius: 10px;
    color: #6d4c41;
    font-size: 1.5rem;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
    transition: transform 0.2s ease-in-out;
}
.tarjeta-categoria:hover{
    transform: scale(1.1);
}
.seccion-destacados, .seccion-testimonios, .seccion-adopcion {
    padding: 40px;
    background-color: #FFF3E0;
}
.seccion-destacados, .contenedor-pasos{
    display: flex;
    justify-content: center;
    gap: 20px;
    flex-wrap: wrap;

}
.tarjeta-mascota, .tarjeta-testimonio, .paso{
    background-color: white;
    border-radius: 10px;
    padding: 15px;
    margin :15px;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 250px;
}
.tarjeta-mascota img {
    width: 100%;
    max-width: 200px;
    object-fit: cover;
    border-radius: 10px;
}
.tarjeta-mascota button{
    background-color: #FF9966;
    border: none;
    color: white;
    padding:10px;
    border-radius: 5px;
    cursor:pointer;
}
.tarjeta-mascota button:hover {
    background-color: #FF7043;
}
.pie-pagina {
    background-color: #8D6E63;
    color: white;
    padding: 10px;
    margin-top: 20px;
}
.contenedor-destacados {
    display: flex;
    justify-content: center; 
    gap: 20px; 
    flex-wrap: wrap;
}
.tarjeta-mascota {
    background-color: white;
    border-radius: 10px;
    padding: 15px;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 280px; 
    display: flex;
    flex-direction: column; 
    align-items: center;
    gap: 10px; 
}
.tarjeta-mascota {
    position: relative;
    overflow: hidden;
}
.tarjeta-mascota h4 {
    position: absolute;
    top: 5 vxpx; 
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.6);
    color: white;
    padding: 5px 10px;
    border-radius: 5px;
    font-size: 1rem;
}
.titulo-destacados {
    text-align: center;
    font-size: 24px;
    font-weight: bold;
    margin-bottom: 20px; 
}
.logo-contenedor {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 5px;
    background-color: #FFF3E0;
    border-radius: 50%;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }
