body{
    font-family: 'Quicksand', sans-serif;
    background-color: #ffe5b4;
    display: flex;
    justify-content: center;
    height: 100vh;
    margin: 0;
    align-items: center;

}
.fondo {
    background-color: white;
    padding: 40px;
    border-radius: 10px;
    box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2); 
    text-align: center;
    width: 400px;
    display: flex;
    flex-direction: column;
    align-items: center;
}
h2{
    color: #6d4c41;
    margin-bottom: 20px;
}
form{
    display: flex;
    flex-direction: column;
    width: 100%;
}
label{
    text-align: left;
    font-weight: bold;
    color: #6d4c41;
    margin-bottom: 5px;
}
input{
    padding: 10px;
    margin-bottom: 15px;
    border: 1px solid #d3a87c;
    border-radius: 5px;
    font-size: 1rem;
    width: 100%;
    
}
button{
    background-color: #FF9966;
    color: white;
    padding: 12px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    width: 100%;
}
button:hover{
    background-color: #FF7043;
}
p {
    margin-top: 15px;
    font-size: 0.9rem;
}

p a {
    color: #FF7043;
    text-decoration: none;
    font-weight: bold;
}

p a:hover {
    text-decoration: underline;
}
.registro-opciones {
    display: flex;
    justify-content: center;
    gap: 10px;
    margin-top: 10px;
}
.registrar {
    background-color: #FF9966;
    color: white;
    border: none;
    padding: 10px 15px;
    cursor: pointer;
    border-radius: 5px;
    transition: background 0.3s;
}
