@@ -0,0 +1,46 @@
+
+body {
+  font-family: 'Arial', sans-serif;
+  background-color: #ffe5b4;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  min-height: 100vh;
+  margin: 0;
+}
+
+
+.alertas {
+  background-color: #ffffff;
+  padding: 40px 30px;
+  border-radius: 12px;
+  box-shadow: 0px 4px 12px rgba(0,0,0,0.2);
+  text-align: center;
+  max-width: 500px;
+  width: 90%;
+  animation: entrada 0.6s ease-out;
+}
+.alertas h1 {
+  font-size: 1.8rem;
+  color: #6d4c41;
+  margin-bottom: 20px;
+}
+
+
+.alertas a {
+  display: inline-block;
+  background-color: #ff9966;
+  color: white;
+  padding: 12px 25px;
+  border-radius: 6px;
+  text-decoration: none;
+  font-weight: bold;
+  transition: background-color 0.3s ease;
+}
+
+.alertas a:hover {
+  background-color: #ff7043;
+}
+
+
+

