body {
  font-family: 'Arial', sans-serif;
  background-color: #ffe5b4;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  margin: 0;
}


.alertas {
  background-color: #ffffff;
  padding: 40px 30px;
  border-radius: 12px;
  box-shadow: 0px 4px 12px rgba(0,0,0,0.2);
  text-align: center;
  max-width: 500px;
  width: 90%;
  animation: entrada 0.6s ease-out;
}
