body {
    font-family: 'Arial', sans-serif;
    background-color: #ffe5b4;
    color: #6d4c41;
    margin: 0;
    padding: 0;
}

section {
    background-color: #fff5e0;
    max-width: 600px;
    margin: 60px auto;
    padding: 30px;
    border-radius: 12px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    text-align: left;
}

p {
    text-align: center;
    font-size: 1.2em;
    margin-bottom: 25px;
}

.campo {
    display: flex;
    flex-direction: column;
    gap: 15px;
}

label {
    font-weight: bold;
    color: #5a3825;
    margin-bottom: 5px;
}

input[type="text"],
input[type="email"],
textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 6px;
    font-size: 1em;
    background-color: #fffdf8;
    box-sizing: border-box;
    transition: border-color 0.3s;
}

input:focus,
textarea:focus {
    border-color: #ff9966;
    outline: none;
}

textarea {
    resize: vertical;
    min-height: 120px;
}

.btn {
    background-color: #ff9966;
    color: white;
    border: none;
    padding: 12px 20px;
    font-size: 1em;
    border-radius: 6px;
    cursor: pointer;
    margin-top: 20px;
    transition: background-color 0.3s ease;
}

.btn:hover {
    background-color: #ff7043;
}
