body {
    font-family: 'Arial', sans-serif;
    background-color: #FFE5B4;
    color: #6D4C41;
    margin: 0;
    text-align: center;
}
header {
    background-color: #FFCC99;
    padding: 15px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
nav a {
    text-decoration: none;
    color: #6D4C41;
    font-weight: bold;
    margin: 0 15px;
}
.hero {
    background-color: #FFDAB9;
    padding: 50px 20px;
}
.filters {
    margin: 20px 0;
}
.filters select, .filters button {
    padding: 10px;
    margin: 5px;
    border-radius: 5px;
    border: 1px solid #6D4C41;
}
.cat-list {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    padding: 20px;
}
.cat-card {
    background: white;
    border-radius: 10px;
    padding: 15px;
    margin: 15px;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 250px;
}

.cat-card img {
    width: 100%;
    border-radius: 10px;
}

.cat-card button {
    background-color: #FF9966;
    border: none;
    color: white;
    padding: 10px;
    border-radius: 5px;
    cursor: pointer;
}
.cat-card button:hover {
    background-color: #FF7043;
}

.footer {
    background-color: #8D6E63;
    color: white;
    padding: 10px;
    margin-top: 20px;
}
#foto_logo{
    width: 120px;
    height: 120px;
    object-fit: contain;
}
.filters {
    background-color: #ffe9c6;
    padding: 20px 30px;
    border-radius: 12px;
    margin: 20px auto;
    max-width: 900px;
    text-align: center;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}
.filters h3 {
    font-size: 1.4em;
    margin-bottom: 15px;
    color: #5a3825;
}
.filters select,
.filters button {
    margin: 5px;
    padding: 10px 15px;
    font-size: 1em;
    border: 1px solid #aaa;
    border-radius: 6px;
    background-color: #fff;
    transition: all 0.3s ease;
}
.filters select:focus,
.filters button:focus {
    outline: none;
    border-color: #d48c47;
    box-shadow: 0 0 5px #d48c47;
}
.filters button {
    background-color: #ffb56b;
    color: #fff;
    font-weight: bold;
    border: none;
    cursor: pointer;
}

.filters button:hover {
    background-color: #e69b51;
}
