body {
    font-family: 'Quicksand', sans-serif;
    background-color: #ffe5b4;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
}
.fondo{
    background-color: white;
    padding: 30px;
    border-radius: 10px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 350px;
}
h2 {
    color: #6d4c41;
    margin-bottom: 20px;
}
form {
    display: flex;
    flex-direction: column;
    align-items: center;
}
label {
    text-align: left;
    font-weight: bold;
    color: #6d4c41;
    margin-top: 10px;
    width: 100%;
}
input {
    padding: 10px;
    margin-top: 5px;
    border: 1px solid #d3a87c;
    border-radius: 5px;
    font-size: 1rem;
    width: 100%;
}
button {
    background-color: #FF9966;
    color: white;
    padding: 12px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    margin-top: 15px;
    width: 100%;
}
button:hover {
    background-color: #FF7043;
}
